mapper.select:
select

from edu_comment
where 1=1

  and uid = #{uid,jdbcType=INTEGER}


  and newtype = #{newtype,jdbcType=INTEGER}


  and newsid = #{newsid,jdbcType=INTEGER}

mapper.insert:
insert into edu_comment (comid, context, newsid, 
  uid, comtime, isshow, newtype, parentid
  )
values (#{comid,jdbcType=INTEGER}, #{context,jdbcType=VARCHAR}, #{newsid,jdbcType=INTEGER}, 
  #{uid,jdbcType=INTEGER}, #{comtime,jdbcType=TIMESTAMP}, #{isshow,jdbcType=INTEGER},
   #{newsid,jdbcType=INTEGER}, #{parentid,jdbcType=INTEGER}
  )
