update.foreach:
update edu_question set
question=#{item.question}, item1=#{item.item1}, item1_num=#{item.item1_num}, item1_grade=#{item.item1_grade}, item1_image= #{item.item1_image}, item2=#{item.item2}, item2_num=#{item.item2_num}, item2_grade=#{item.item2_grade}, item2_image= #{item.item2_image}, item3=#{item.item3}, item3_num=#{item.item3_num}, item3_grade=#{item.item3_grade}, item3_image= #{item.item3_image}, item4=#{item.item4}, item4_num=#{item.item4_num}, item4_grade=#{item.item4_grade}, item4_image= #{item.item4_image}, item5=#{item.item5}, item5_num=#{item.item5_num}, item5_grade=#{item.item5_grade}, item5_image= #{item.item5_image}, item6=#{item.item6}, item6_num=#{item.item6_num}, item6_grade=#{item.item6_grade}, item6_image= #{item.item6_image}
where qid= #{item.qid}

mapper.select:
select s.sid, s.schoolId, s.search_name as searchName,s.content,s.createtime,
s.updatetime,s.endtime,s.schoolName,
s.top_image as topImage,s.author,s.tid
from edu_search_question s

where 1=1

    AND s.sid = #{sid}


    AND s.schoolId = #{schoolId}


    AND s.tid = #{tid}





    AND (s.searchName like CONCAT('%',#{search},'%') or s.author like CONCAT('%',#{search},'%'))


ORDER BY s.createtime DESC,s.updatetime DESC

select q.qid,q.question,q.item1,q.item1_num,q.item1_image,q.item2,q.item2_num ,q.item2_image,q.item3,q.item3_num,q.item3_image,q.item4,q.item4_num,q.item4_image,q.item5,q.item5_num,q.item5_image,q.item6,q.item6_num,q.item6_image,q.tid from edu_question q where 1=1
AND q.sid = #{sid}
AND q.tid = #{tid}
AND (s.searchName like CONCAT('%',#{search},'%') or s.author like CONCAT('%',#{search},'%'))

select q.qid,q.question,q.item1,q.item1_num,item1_grade,q.item1_image,q.item2,q.item2_num,item2_grade ,q.item2_image,q.item3,q.item3_num,item3_grade,q.item3_image,q.item4,q.item4_num,item4_grade,q.item4_image,q.item5,q.item5_num,item5_grade,q.item5_image,q.item6,q.item6_num,item6_grade,q.item6_image,q.tid from edu_question q
where 1=1
AND q.sid = #{sid}
AND q.tid = #{tid}
AND (s.searchName like CONCAT('%',#{search},'%') or s.author like CONCAT('%',#{search},'%'))
