mapper.select:
select co.course_name as courseName, va.vacate_id as vacateId, va.vacate_time as vacateTime, va.contacts as contacts, va.contacts_phone as contactsPhone, va.vacate_name as vacateName, va.vacate_cause as vacateCause, (select dict_name from edu_dict where dict_type='VACATE_TYPE' and dict_value = va.vacate_type) as vacateTypeName from edu_vacate as va LEFT JOIN edu_course as co ON va.course_id = co.course_id where 1=1
AND co.school_id = #{schoolId}
order by va.vacate_time DESC

select

from edu_course
where 1=1

    AND school_id = #{schoolId}


    AND isenable = #{isenable}


     and  start_date >= #{startDate}


     and  start_date <= #{endDate}


    AND course_name like CONCAT('%',#{search},'%')

order by isenable desc, start_date desc

select course_id as courseId, course_name as courseName, course_price as coursePrice, course_teacher as courseTeacher,
place_class as placeClass, start_date as startDate, end_date as endDate,
people_number as peopleNumber, school_id as schoolId, isenable,
(SELECT name FROM edu_admin where admin_id = edu_course.course_teacher) as teacherName,
(SELECT school_name FROM edu_school where school_id = edu_course.school_id) as schoolName
from edu_course
where 1=1

    AND school_id = #{schoolId}


    AND isenable = #{isenable}


     and  start_date >= #{startDate}


     and  start_date <= #{endDate}


    AND course_name like CONCAT('%',#{search},'%')

order by isenable desc, start_date desc

mapper.insert:
insert into edu_course (course_name, course_price,
  course_teacher, place_class, start_date,
  end_date, people_number, school_id, isenable
  )
values (#{courseName,jdbcType=VARCHAR}, #{coursePrice,jdbcType=DECIMAL},
  #{courseTeacher,jdbcType=VARCHAR}, #{placeClass,jdbcType=VARCHAR}, #{startDate,jdbcType=DATE},
  #{endDate,jdbcType=DATE}, #{peopleNumber,jdbcType=INTEGER}, #{schoolId,jdbcType=VARCHAR}, #{isenable,jdbcType=INTEGER}
  )

mapper.select:
select class_schedule_id as classScheduleId, course_ids as courseIds, week, start_time as startTime, end_time as endTime
from edu_class_schedule
where course_ids = #{courseId}

select u.user_id userId,i.user_name userName,u.phone,u.image,i.user_item_id userItemId, i.sex,i.school_id schoolId,i.telephone,i.area,i.address, i.political,i.educational,i.position,i.retired, i.job,i.employer,i.emergency_contact1 emergencyContact1,i.emergency_relation1 emergencyRelation1, i.emergency_phone1 emergencyPhone1,i.emergency_contact2 emergencyContact2, i.emergency_relation2 emergencyRelation2,i.emergency_phone2 emergencyPhone2,i.checkin, i.identity_card identityCard,i.volunteer,i.user_item_id userItemId from edu_user u left join edu_user_item i on u.user_id = i.user_id where 1 =1
and i.school_id = #{schoolId}
AND u.user_name like CONCAT('%',#{search},'%') or u.phone like CONCAT('%',#{search},'%'))

select * from edu_withdraw where 1 =1
and school_id=#{schoolId}
AND user_name like CONCAT('%',#{search},'%')

select

from edu_course
where isenable != -1  and  end_date <= #{endDate,jdbcType=DATE}
