mapper.select:
select *
from  edu_user
where  user_id=#{userId};

mapper.update:
update edu_user_item
user_name=#{userName},school_id=#{schoolId},sex=#{sex},telephone=#{telephone},areaId=#{areaId},area=#{area}, address=#{address},political=#{political},educational=#{educational},position=#{position},retired=#{retired}, employer=#{employer},job=#{job},emergency_contact1=#{emergencyContact1}, emergency_relation1=#{emergencyRelation1},emergency_phone1=#{emergencyPhone1}, emergency_contact2=#{emergencyContact2},emergency_relation2=#{emergencyRelation2}, emergency_phone2=#{emergencyPhone2},volunteer=#{volunteer},identity_card=#{identityCard},
school_job=#{schoolJob},
remark=#{remark}
where user_id=#{userId}

mapper.select:
select  w.wid wid,w.uid uid,w.school_id,w.school school,w.content content,w.checkin checkin
  from  edu_withdraw w
     where w.school_id=#{schoolId} and w.checkin=#{checkin}

select * from edu_contribute where 1=1

    and school_id=#{schoolId}


    AND user_name like CONCAT('%',#{search},'%')

mapper.update:
update edu_contribute 

 checkin=#{checkin},


      reject_reason=#{rejectReason}


 where

cid=#{cid}

mapper.select:
select COUNT(*)
from edu_user_item
where school_id = #{schoolId}

    and sex = #{sex}


    and political = #{political}
